"use strict";const bubblegum_prefix="_";function checkElements(t){t.querySelectorAll("*").forEach((t=>{const e=t.attributes.length;if(e>0)for(let s=0;s<e;s++){let e=t.attributes[s].name;if("_"===e.charAt(0)){e=e.slice(1);let a=e;t.attributes[s].nodeValue.length>0?t.attributes[s].nodeValue.split(" ").forEach((e=>{"!"===e?t.classList.add(a):t.classList.add(a+"-"+e)})):t.classList.add(a),t.removeAttribute(t.attributes[s].name)}}}))}window.addEventListener("load",(function(){checkElements(document.querySelector("body"))}));